import React, { useEffect, useState } from 'react';

function App() {
    const [transactions, setTransactions] = useState([]);
    const [budgets, setBudgets] = useState([]);
    const [form, setForm] = useState({ amount: '', category: '', description: '', date: '', id: '' });

    useEffect(() => {
        fetchTransactions();
        fetchBudgets();
    }, []);

    const fetchTransactions = async () => {
        const response = await fetch('http://localhost:5001/transactions');
        const data = await response.json();
        setTransactions(data);
    };

    const fetchBudgets = async () => {
        const response = await fetch('http://localhost:5001/budgets');
        const data = await response.json();
        setBudgets(data);
    };

    const addTransaction = async () => {
        const transaction = {
            user_id: '123', // Mock user ID for now
            ...form,
        };

        try {
            const response = form.id
                ? await fetch(`http://localhost:5001/transactions/${form.id}`, {
                      method: 'PUT',
                      headers: { 'Content-Type': 'application/json' },
                      body: JSON.stringify(transaction),
                  })
                : await fetch('http://localhost:5001/transactions', {
                      method: 'POST',
                      headers: { 'Content-Type': 'application/json' },
                      body: JSON.stringify(transaction),
                  });

            if (response.ok) {
                console.log(form.id ? 'Transaction updated' : 'Transaction added:', await response.json());
                setForm({ amount: '', category: '', description: '', date: '', id: '' });
                fetchTransactions();
            } else {
                console.error('Error adding or updating transaction:', response.statusText);
            }
        } catch (error) {
            console.error('Error adding or updating transaction:', error);
        }
    };

    const deleteTransaction = async (id) => {
        try {
            const response = await fetch(`http://localhost:5001/transactions/${id}`, {
                method: 'DELETE',
            });

            if (response.ok) {
                console.log('Transaction deleted successfully');
                setTransactions(transactions.filter((t) => t._id !== id));
            } else {
                console.error('Error deleting transaction:', response.statusText);
            }
        } catch (error) {
            console.error('Error deleting transaction:', error);
        }
    };

    const editTransaction = (transaction) => {
        setForm({
            amount: transaction.amount,
            category: transaction.category,
            description: transaction.description,
            date: transaction.date,
            id: transaction._id,
        });
    };

    return (
        <div>
            <header className="logo-header">
                <img src="/logo.png" alt="FinWise Logo" className="logo" />
            </header>
            <h1>Personal Finance Dashboard</h1>
            <section>
                <h2>{form.id ? 'Edit Transaction' : 'Add Transaction'}</h2>
                <input
                    type="number"
                    placeholder="Amount"
                    value={form.amount}
                    onChange={(e) => setForm({ ...form, amount: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="Category"
                    value={form.category}
                    onChange={(e) => setForm({ ...form, category: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="Description"
                    value={form.description}
                    onChange={(e) => setForm({ ...form, description: e.target.value })}
                />
                <input
                    type="date"
                    value={form.date}
                    onChange={(e) => setForm({ ...form, date: e.target.value })}
                />
                <button onClick={addTransaction}>
                    {form.id ? 'Update Transaction' : 'Add Transaction'}
                </button>
            </section>
            <section>
                <h2>Transactions</h2>
                <ul>
                    {transactions.map((t) => (
                        <li key={t._id} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                            <div>
                                <strong>{new Date(t.date).toLocaleDateString('en-US')}</strong> | ${t.amount} - {t.category} ({t.description})
                            </div>
                            <button
                                style={{ marginLeft: '10px', backgroundColor: 'blue', color: 'white', border: 'none', cursor: 'pointer', fontSize: '12px' }}
                                onClick={() => editTransaction(t)}
                            >
                                Edit
                            </button>
                            <button
                                style={{ marginLeft: '10px', backgroundColor: 'red', color: 'white', border: 'none', cursor: 'pointer', fontSize: '12px' }}
                                onClick={() => deleteTransaction(t._id)}
                            >
                                Delete
                            </button>
                        </li>
                    ))}
                </ul>
            </section>
            <section>
                <h2>Budgets</h2>
                <ul>
                    {budgets.map((b) => (
                        <li key={b._id}>
                            {b.category}: ${b.amount} (Ends: {new Date(b.end_date).toLocaleDateString()})
                        </li>
                    ))}
                </ul>
            </section>
        </div>
    );
}

export default App;
