// import React from 'react';
// import ReactDOM from 'react-dom';
// import './index.css'; // Ensure this file exists; if not, remove this line
// import App from './App';

// ReactDOM.render(
//   <React.StrictMode>
//     <App />
//   </React.StrictMode>,
//   document.getElementById('root')
// );

import React, { useEffect, useState } from 'react';

function App() {
    const [transactions, setTransactions] = useState([]);
    const [budgets, setBudgets] = useState([]);
    const [form, setForm] = useState({ amount: '', category: '', description: '' });

    useEffect(() => {
        fetchTransactions();
        fetchBudgets();
    }, []);

    const fetchTransactions = async () => {
        const response = await fetch('http://localhost:5000/transactions');
        const data = await response.json();
        setTransactions(data);
    };

    const fetchBudgets = async () => {
        const response = await fetch('http://localhost:5000/budgets');
        const data = await response.json();
        setBudgets(data);
    };

    const addTransaction = async () => {
        await fetch('http://localhost:5000/transactions', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(form),
        });
        setForm({ amount: '', category: '', description: '' });
        fetchTransactions();
    };

    return (
        <div>
            <h1>Personal Finance Dashboard</h1>
            <section>
                <h2>Add Transaction</h2>
                <input 
                    type="number" 
                    placeholder="Amount" 
                    value={form.amount} 
                    onChange={(e) => setForm({ ...form, amount: e.target.value })}
                />
                <input 
                    type="text" 
                    placeholder="Category" 
                    value={form.category} 
                    onChange={(e) => setForm({ ...form, category: e.target.value })}
                />
                <input 
                    type="text" 
                    placeholder="Description" 
                    value={form.description} 
                    onChange={(e) => setForm({ ...form, description: e.target.value })}
                />
                <button onClick={addTransaction}>Add Transaction</button>
            </section>
            <section>
                <h2>Transactions</h2>
                <ul>
                    {transactions.map((t) => (
                        <li key={t._id}>
                            ${t.amount} - {t.category} ({t.description})
                        </li>
                    ))}
                </ul>
            </section>
            <section>
                <h2>Budgets</h2>
                <ul>
                    {budgets.map((b) => (
                        <li key={b._id}>
                            {b.category}: ${b.amount} (Ends: {new Date(b.end_date).toLocaleDateString()})
                        </li>
                    ))}
                </ul>
            </section>
        </div>
    );
}

export default App;
